import React, { useState } from "react";
import "./App.css";

const App = () => {
  const [activeButton, setActiveButton] = useState(null);

  const questions = {
    button1: ["Question 1.1", "Question 1.2", "Question 1.3"],
    button2: ["Question 2.1", "Question 2.2", "Question 2.3"],
    button3: ["Question 3.1", "Question 3.2", "Question 3.3"],
  };

  const handleButtonClick = (button) => {
    setActiveButton(button);
  };

  const getBackgroundColor = () => {
    switch (activeButton) {
      case "button1":
        return "url('./img/dent1.jpg')"; // Gold
      case "button2":
        return "url('./img/dent2.jpg')"; // Sky Blue
      case "button3":
        return "url('./img/dent3.jpg')"; // Pale Green
      default:
        return "url('./img/dent1.jpg')"; // White
    }
  };

  return (
    <div className="App" style={{
        backgroundImage: getBackgroundImage(),
        backgroundSize: "cover", // Ensures the image covers the entire area
        backgroundPosition: "center", // Centers the image
        backgroundRepeat: "no-repeat", // Prevents tiling
      }}>
      <Navbar />
      <Header />
      <div className="button-container">
        <button onClick={() => handleButtonClick("button1")}>Button 1</button>
        <button onClick={() => handleButtonClick("button2")}>Button 2</button>
        <button onClick={() => handleButtonClick("button3")}>Button 3</button>
      </div>
      <div className="question-list">
        {activeButton &&
          questions[activeButton].map((question, index) => (
            <div key={index}>
              <label>
                <input type="checkbox" /> {question}
              </label>
            </div>
          ))}
      </div>
      <Footer />
    </div>
  );
};

const Navbar = () => (
  <nav className="navbar">
    <h2>My React App</h2>
  </nav>
);

const Header = () => (
  <header className="header">
    <h1>Welcome to the App</h1>
  </header>
);

const Footer = () => (
  <footer className="footer">
    <p>&copy; 2024 My React App</p>
  </footer>
);

export default App;
