import React, { useState } from "react";
import Navbar from "./Navbar"; // Import the Navbar component
import "./App.css";

const App = () => {
  const [selectedSection, setSelectedSection] = useState("");

  // Questions for each section
  const questions = {
    doctors: [
      "What symptoms does the patient have?",
      "What are the patient's medical records?",
      "What is the preliminary diagnosis?",
    ],
    clinics: [
      "What equipment is needed?",
      "Are there any recurring patient issues?",
      "What dental software is being used?",
    ],
    laboratories: [
      "What tests are required?",
      "What is the expected delivery date for test results?",
      "Are additional materials needed?",
    ],
  };

  return (
    <div className="app-container">
      <Navbar />
      <div className="header">
        <h1>Dental Expert System</h1>
      </div>
      <div className="section-buttons">
        <button
          className="button-link"
          onClick={() => setSelectedSection("doctors")}
        >
          For Doctors
        </button>
        <button
          className="button-link"
          onClick={() => setSelectedSection("clinics")}
        >
          For Clinics and DSO
        </button>
        <button
          className="button-link"
          onClick={() => setSelectedSection("laboratories")}
        >
          For Laboratories
        </button>
      </div>
      <div className="questions-section">
        {selectedSection && (
          <div>
            <h2>
              {selectedSection === "doctors"
                ? "Questions for Doctors"
                : selectedSection === "clinics"
                ? "Questions for Clinics"
                : "Questions for Laboratories"}
            </h2>
            <ul>
              {questions[selectedSection].map((question, index) => (
                <li key={index}>{question}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
