import React, { useState } from "react";
import "./App.css";

const App = () => {
  const [activeButton, setActiveButton] = useState(null);

  const questions = {
    button1: ["Question 1.1", "Question 1.2", "Question 1.3"],
    button2: ["Question 2.1", "Question 2.2", "Question 2.3"],
    button3: ["Question 3.1", "Question 3.2", "Question 3.3"],
  };

  const handleButtonClick = (button) => {
    setActiveButton(button);
  };

  const getBackgroundImage = () => {
    switch (activeButton) {
      case "button1":
        return "url('../img/dent1.jpg')";
      case "button2":
        return "url('../img/dent2.jpg')";
      case "button3":
        return "url('img/dent3.jpg')";
      default:
        return "url('img/dent1.jpg')";
    }
  };

  return (
    <div
      className="App"
      style={{
        backgroundImage: "url('dent1.jpg')",//getBackgroundImage(),
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
      }}
    >
      <Header handleButtonClick={handleButtonClick} />
      <div className="question-list">
        {activeButton &&
          questions[activeButton].map((question, index) => (
            <div key={index}>
              <label>
                <input type="checkbox" /> {question}
              </label>
            </div>
          ))}
      </div>
      <Footer />
    </div>
  );
};

const Header = ({ handleButtonClick }) => (
  <header className="header">
    <nav className="navbar">
      <h2>My React App</h2>
    </nav>
    <div className="button-container">
      <button onClick={() => handleButtonClick("button1")}>Button 1</button>
      <button onClick={() => handleButtonClick("button2")}>Button 2</button>
      <button onClick={() => handleButtonClick("button3")}>Button 3</button>
    </div>
  </header>
);

const Footer = () => (
  <footer className="footer">
    <p>&copy; 2024 My React App</p>
  </footer>
);

export default App;
