import React, { useState } from "react";
import Layout from "./layout.js"; // Import the Layout component
import "./DiseasesPage.css"; // Import the CSS for styling

const DiseasesPage = () => {
  const diseases = [
    { code: "k0001", name: "Abscess Periodontal", description: "A periodontal abscess is a localized infection that forms in the tissues surrounding a tooth, often resulting from bacterial infection in a pocket of gum tissue. It typically occurs in individuals with existing gum disease, such as periodontitis. This condition is marked by swollen, red, and painful gums, often accompanied by fever and a sensation of throbbing pain, particularly when chewing. A visual representation of a periodontal abscess would include inflamed gums, with a localized bump or pimple-like formation on the gum surface near the affected tooth, with redness and swelling.", image: "/img/PeriodondalAbscess.jpg" },
    { code: "k0002", name: "Abscess Periapical", description: "Periapical abscesses occur at the tip of the tooth’s root and are usually caused by untreated cavities, which allow bacteria to infiltrate the inner part of the tooth (pulp). This leads to severe pain, swelling, and possible fever, along with sensitivity to hot and cold. Affected teeth may feel loose, and there may be visible pus. An image of this condition would focus on a swollen area at the base of the tooth, showing redness and possible drainage of pus, as well as the classic tooth root involvement.", image: "/img/Abscess_Periapical.jpg" },
    { code: "k0003", name: "Anodontia", description: "Anodontia (K0003): Anodontia is a rare congenital condition where one or more teeth are absent at birth. It can affect both primary (baby) teeth and permanent teeth. This disorder can cause difficulty in chewing, speaking, and can impact a person’s appearance. It may be hereditary or related to genetic syndromes. The image of anodontia would feature a mouth with visible gaps where teeth should be, highlighting the absence of teeth, and showing how this condition affects facial aesthetics.", image: "path/to/image3.jpg" },
    { code: "k0004", name: "Tooth Abrasion", description: " Tooth abrasion happens when the enamel of the teeth is worn away due to external factors such as aggressive brushing, excessive use of hard-bristled toothbrushes, or teeth grinding. This leads to tooth sensitivity, especially to hot or cold foods, and a noticeable thinning or worn-down appearance on the teeth. An image of tooth abrasion would show the surface of the teeth with visible wear marks, possibly along the gumline or on the biting surfaces, with an emphasis on the damage caused to enamel.", image: "path/to/image4.jpg" },
    { code: "k0005", name: "Bruxism", description: "Bruxism refers to the grinding or clenching of teeth, typically during sleep. This condition can lead to worn-down teeth, jaw pain, headaches, and sensitivity. Over time, bruxism may cause the enamel to erode, resulting in visible flattening or chipping of the teeth. A typical image for bruxism would show a worn-down surface of teeth with noticeable flattening or cracking, alongside the involvement of jaw muscles, potentially in a diagram showing the tension and pressure applied during grinding.", image: "path/to/image5.jpg" },
    { code: "k0006", name: "Caries", description: "Gingivitis is the mildest form of gum disease, causing irritation, redness, and swelling of the gums due to plaque buildup. If left untreated, gingivitis can progress to more severe periodontal diseases. The main symptoms include red, swollen gums that may bleed easily when brushing or flossing. An image of gingivitis would show inflamed gums around the teeth, with redness and possible bleeding at the gumline, highlighting the early stages of gum disease.", image: "path/to/image6.jpg" },
    { code: "k0007", name: "Dental Erosion", description: "Purulent gums are a sign of a serious gum infection, often associated with periodontitis or abscesses, where pus accumulates in the gum tissues. This condition is marked by swollen, painful gums, with visible pus oozing from the gums, and an unpleasant odor. An image for this condition would show visibly swollen, infected gums with yellow or greenish pus present, highlighting the severity of the infection and the need for medical intervention.", image: "path/to/image7.jpg" },
    { code: "k0008", name: "Gingivitis", description: "A perforated tooth occurs when a cavity or trauma creates a hole or crack in the tooth, leading to infection if left untreated. The symptoms include pain, sensitivity, and visible cracks or holes in the affected tooth. In more severe cases, an abscess may form at the site of the perforation. The image would focus on a tooth with a visible crack or hole, possibly with signs of infection around the edges of the crack.", image: "path/to/image8.jpg" },
    { code: "k0009", name: "Halitosis", description: "Tooth fractures are commonly caused by trauma, such as biting hard objects or accidents. These fractures can range from minor chips to severe breaks that compromise the structure and function of the tooth. Symptoms of a fractured tooth include pain, difficulty chewing, and visible cracks or breaks. The image would depict a tooth with a visible fracture or crack, showing the extent of damage to the tooth and possible accompanying swelling or discoloration.", image: "path/to/image9.jpg" },
    { code: "k0010", name: "Oral Cancer", description: "Periodontitis is a severe form of gum disease that damages the soft tissues and destroys the bone supporting the teeth. This condition is a major cause of tooth loss in adults. The symptoms include swollen, bleeding gums, gum recession, and loose teeth, often accompanied by bad breath. A visual depiction of periodontitis would show receding gums, loss of bone support around the teeth, and potentially loose teeth, emphasizing the advanced stage of the disease.", image: "path/to/image10.jpg" },
  ];

  const [selectedDisease, setSelectedDisease] = useState(diseases[0]);

  return (
    <Layout>
      <div className="diseases-page">
        <div className="side-nav">
          <h2>Diseases</h2>
          <ul>
            {diseases.map((disease) => (
              <li key={disease.code} onClick={() => setSelectedDisease(disease)}>
                {disease.name}
              </li>
            ))}
          </ul>
        </div>
        <div className="disease-details">
          <div><h1>{selectedDisease.name}</h1>
          <p>{selectedDisease.description}</p></div>
          <img src={selectedDisease.image} alt={selectedDisease.name} className="disease-image" />
        </div>
      </div>
    </Layout>
  );
};

export default DiseasesPage;
